example:
{
  key: 'email',
  type: 'input',
  label: 'Email Address',
  required: true,
  email: true,
  props: { type: 'email', placeholder: 'user@example.com' }
}

{
  key: 'description',
  type: 'textarea',
  label: 'Description',
  props: { rows: 5, placeholder: 'Enter description...' }
}

{
  key: 'country',
  type: 'select',
  label: 'Country',
  required: true,
  props: {
    options: [
      { label: 'United States', value: 'us' },
      { label: 'Canada', value: 'ca' }
    ]
  }
}

{
  key: 'acceptTerms',
  type: 'checkbox',
  label: 'I accept the terms and conditions',
  required: true
}

{
  key: 'gender',
  type: 'radio',
  label: 'Gender',
  props: {
    options: [
      { label: 'Male', value: 'male' },
      { label: 'Female', value: 'female' },
      { label: 'Other', value: 'other' }
    ]
  }
}

{
  key: 'birthDate',
  type: 'datepicker',
  label: 'Date of Birth',
  required: true,
  props: { maxDate: new Date() }
}

{
  key: 'volume',
  type: 'slider',
  label: 'Volume',
  value: 50,
  props: { min: 0, max: 100, step: 5 }
}

{
  key: 'sectionTitle',
  type: 'text',
  props: { content: 'Personal Information', element: 'h2' }
}

{
  key: 'nameRow',
  type: 'row',
  fields: [
    { key: 'firstName', type: 'input', label: 'First Name', col: 6 },
    { key: 'lastName', type: 'input', label: 'Last Name', col: 6 }
  ]
}

{
  key: 'address',
  type: 'group',
  label: 'Address',
  fields: [
    { key: 'street', type: 'input', label: 'Street' },
    { key: 'city', type: 'input', label: 'City' },
    { key: 'zip', type: 'input', label: 'ZIP Code' }
  ]
}

{
  key: 'phones',
  type: 'array',
  label: 'Phone Numbers',
  template: [
    { key: 'type', type: 'select', label: 'Type', props: { options: [...] } },
    { key: 'number', type: 'input', label: 'Number' }
  ],
  props: { minItems: 1, maxItems: 5 }
}

{
  key: 'step1',
  type: 'page',
  label: 'Personal Info',
  fields: [
    { key: 'firstName', type: 'input', label: 'First Name' },
    { key: 'lastName', type: 'input', label: 'Last Name' }
  ]
}

{
  key: 'customAction',
  type: 'button',
  label: 'Custom Action',
  props: { buttonType: 'button', onClick: 'handleCustomAction' }
}
